import React from "react"
import ReactDOMClient from "react-dom/client"
import { k } from './tariffs'
import { SetMainPage } from './index'

function getCookie(cname) {
  var name = cname + "=";
  var ca = document.cookie.split(';');
  for (var i = 0; i < ca.length; i++) {
    var c = ca[i];
    while (c.charAt(0) === ' ') {
      c = c.substring(1);
    }
    if (c.indexOf(name) === 0) {
      return c.substring(name.length, c.length);
    }
  }
  return "";
}

function Iii() {
  return (
    <div>
      <h1>Личный кабинет</h1>
      <p id="ul">Ваш логин: {getCookie("login")}</p>
      <p id="utn">Ваш тариф: {getCookie("tName")}</p>
      <p id="uts">Скорость тарифа: {getCookie("tSpeed")} Mbit/s</p>
      <p id="utc">Стоимость тарифа: {getCookie("tCost")} руб.</p>
      <button onClick={k}>Сменить тариф</button>
      <button onClick={exit}>Выйти</button>
    </div>
  )
}

function exit() {
  document.cookie = "login=0;max-age=0";
  app.render(<SetMainPage />);
}

const app = ReactDOMClient.createRoot(document.getElementById("app"))

export function SetUserPage() {

  const request = new XMLHttpRequest();

  const url = "http://localhost:8080/lk";

  const params = "login=" + getCookie("login") + "&password=" + getCookie("password");

  request.open("POST", url, true);

  request.setRequestHeader("Content-type", "application/x-www-form-urlencoded");

  request.addEventListener("readystatechange", () => {

    if (request.readyState === 4 && request.status === 200) {
      const obj = JSON.parse(request.responseText);
      document.cookie = "tName=" + obj.Name;
      document.cookie = "tSpeed=" + obj.Speed;
      document.cookie = "tCost=" + obj.Cost;
      app.render(<Iii />);
    }
  });

  request.send(params);
}

export function gc(cname) {
  return getCookie(cname);
}
