import React from "react"
import ReactDOMClient from "react-dom/client"
import { useState } from "react"

var obj;

var ta = [];

var t1, t2, t3;

var idt = 0;

function setV(props) {
    //console.log(props);
    //console.log(ta);
    var tariff = ta.find(el => el.Name == props);
    //console.log(tariff);

    t1 = tariff.Name;
    t2 = tariff.Speed;
    t3 = tariff.Cost;

    idt = tariff.ID;
}

function Chtariff(props) {

    const [value, setValue] = useState('');

    const options = props.t.map((item) => {
        return <option key={item.ID}>{item.Name}</option>;
    });

    return (
        <div id="controlTariff">
            <div id="chtariff1">
                <p>Добавить тариф</p>
                <label htmlFor="nameTariff">Название </label>
                <input type="text" id="nameTariff"></input>
                <br></br><br></br>
                <label htmlFor="speedTariff">Скорость </label>
                <input type="text" id="speedTariff"></input>
                <br></br><br></br>
                <label htmlFor="costTariff">Стоимость </label>
                <input type="text" id="costTariff"></input>
                <br></br><br></br>
                <button>Добавить</button>
            </div>

            <div id="chtariff1">
                <p>Изменение тарифа</p>
                <label htmlFor="nameTariffch">Название </label>
                <input defaultValue="Initial text" type="text" id="nameTariffch" value={t1}></input>
                <br></br><br></br>
                <label htmlFor="speedTariffch">Скорость </label>
                <input type="text" id="speedTariffch" value={t2}></input>
                <br></br><br></br>
                <label htmlFor="costTariffch">Стоимость </label>
                <input type="text" id="costTariffch" value={t3}></input>
                <br></br><br></br>
                <button>Изменить</button>
            </div>

            <div>
                <p>Список тарифов</p>
                    <select id="listTariff" size="3" value={value} onChange={(event) => { setValue(event.target.value); setV(event.target.value) }}>
                        {options}
                    </select>
            </div>
        </div>
    )
}

export function chtariffset() {
    const request = new XMLHttpRequest();

    const url = "http://localhost:8080/tariffs";

    request.open("GET", url, true);

    request.setRequestHeader("Content-type", "application/x-www-form-urlencoded");

    request.addEventListener("readystatechange", () => {

        if (request.readyState === 4 && request.status === 200) {
            var answer = request.responseText;
            var array = answer.split(';');

            for (let i = 1; i < array.length; i++) {
                obj = JSON.parse(array[i]);
                ta.push(obj);
            }
        }
        const fullinfo = ReactDOMClient.createRoot(document.getElementById("fullinfo"));
    fullinfo.render(<Chtariff t = {ta}/>);
    });

    request.send();
}
