import ReactDOMClient from "react-dom/client"
import React, { useState, useRef, useEffect, useCallback } from "react";

const AppWs = () => {
    const [isPaused, setIsPaused] = useState(false);
    const [data, setData] = useState(null);
    const [status, setStatus] = useState("");
    const ws = useRef(null);

    var clientMessage = {
        username: "username",
        destination: "destination",
        message: "message"
    }

    useEffect(() => {
        if (!isPaused) {
            ws.current = new WebSocket("ws:localhost:8080/ws"); // создаем ws соединение
            ws.current.onopen = () => setStatus("Соединение открыто");  // callback на ивент открытия соединения
            ws.current.onclose = () => setStatus("Соединение закрыто"); // callback на ивент закрытия соединения
            gettingData();
        }

        return () => ws.current.close(); // кода меняется isPaused - соединение закрывается
    }, [ws, isPaused]);

    const gettingData = useCallback(() => {
        if (!ws.current) return;

        ws.current.onmessage = e => {//подписка на получение данных по вебсокету
            var a = JSON.parse(e.data, function (key, value) {
                if (key == 'username') return new String(value);
                return value;
            });
            setData(a);
            document.getElementById("chat").textContent += "\n" + a.username + ": " + a.message + "\n";
        };
    });

    function sm() {
        clientMessage.username = document.getElementById("nnn").value;
        clientMessage.destination = "Client";
        clientMessage.message = document.getElementById("bs").value;
        ws.current.send(JSON.stringify(clientMessage));
    }

    return (
        <>
            {!!data &&
                <div>
                    <div>
                        <h2>{status}</h2>
                    </div>
                    <button onClick={() => {
                        ws.current.close();
                        setIsPaused(!isPaused)
                    }}>{!isPaused ? 'Остановить соединение' : 'Открыть соединение'}</button>
                    <div>
                        <p>Ваше имя <input type="text" size="20" id="nnn" /></p>
                        <input type="text" size="40" id="bs" />
                    </div>
                    <button onClick={sm}>Отправить сообщение на сервер</button>
                    <p id="chat">Chat</p>
                </div>
            }
        </>
    )
}

function k() {
    return <div>
    <h1>Главная страница</h1>
    <p onClick={k}>Вывести все тарифы</p>
    <p onClick={SetLogin}>Вход</p>
    <p onClick={SetReg}>Регистрация</p>
    <p></p>
    <p>Посещения: {gc("v")}</p>
  </div>
}

const app = ReactDOMClient.createRoot(document.getElementById("app"))

export function VideoChat() {
    app.render(<AppWs />)
    app.render(<k />)
}
